    public function getSourceContext()
    {
        return new Source("{% extends 'no-sidebar.html.twig' %}

{% block title %}Book index{% endblock %}
{% block pageHeader %}Book Index{% endblock %}
{% block body %}
    <h1>Book index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Author</th>
                <th>Description</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for book in books %}
            <tr>
                <td>{{ book.id }}</td>
                <td>{{ book.name }}</td>
                <td>{{ book.author }}</td>
                <td>{{ book.description }}</td>
                <td>
                    <a href=\"{{ path('book_show', {'id': book.id}) }}\"><button type=\"button\" class=\"btn btn-primary btn-sm\" >Show</button></a>
                    <a href=\"{{ path('book_edit', {'id': book.id}) }}\"><button type=\"button\" class=\"btn btn-primary btn-sm\" >Edit</button></a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
    <a href=\"{{ path('book_new') }}\"><button type=\"button\" class=\"btn btn-primary btn-sm\">Create New</button></a>
    <br>
    <br>
    <a class=\"button\" href=\"{{ url('index') }}\" style=\"display: block; text-align: center;\">Back To Home Page</a>

{% endblock %}
", "book/index.html.twig", "/Users/jeremiah/Desktop/BookAuction/templates/book/index.html.twig");
